import React, { useState } from 'react';

const Login = () => {
	const [user, setUser] = useState({
		name: '',
		email: '',
		password: '',
	});

	const [error, setError] = useState('');
	const onChangeHandler = (e) => {
		const { name, value } = e.target;
		setUser({ ...user, [name]: value });
		setError('');
	};

	const registerSubmit = async (e) => {
		e.preventDefault();

		try {
			
		} catch (error) {
			error.response.data.msg && setError()
		}
	};

	return (
		<section>
			<div className="login">
				<h2>Login</h2>
				<form>
					<input
						type="text"
						name="email"
						id="login-email"
						placeholder="E-mail"
						required
						valu={user.email}
						onChange={onChangeHandler}
					/>
					<input
						type="password"
						name="password"
						id="login-password"
						required
						valu={user.password}
						autoComplete="true"
						onChange={onChangeHandler}
					/>
					<button type="submit">Login</button>
					<p>
						you don't have an acoount <span>Register Now</span>?
					</p>
					<h3>{error}</h3>
				</form>
			</div>

			<div className="register">
				<h2>Register</h2>
				<form onSubmit={registerSubmit}>
					<input
						type="text"
						name="name"
						id="register-name"
						placeholder="E-mail"
						required
						valu={user.name}
						onChange={onChangeHandler}
					/>
					<input
						type="text"
						name="email"
						id="register-email"
						placeholder="E-mail"
						required
						valu={user.email}
						onChange={onChangeHandler}
					/>
					<input
						type="password"
						name="password"
						id="register-password"
						required
						valu={user.password}
						autoComplete="true"
						onChange={onChangeHandler}
					/>
					<button type="submit">Register</button>
					<p>
						you don't have an acoount <span>Login Now</span>?
					</p>
					<h3>{error}</h3>
				</form>
			</div>
		</section>
	);
};

export default Login;
